        pos.insert("(x + y)^2".to_string(), handle_mult_constraint(&parsed_poly, pos["(x + y)"], pos["(x + y)"]));  
        pos.insert("(x + y)^3".to_string(), handle_mult_constraint(&parsed_poly, pos["(x + y)"], pos["(x + y)^2"]));
