        pos.insert("x^5".to_string(), handle_mult_constraint(&parsed_poly, pos["x"], pos["x^4"]));
        pos.insert("7x^5".to_string(), handle_const_mult_constraint(&parsed_poly, 7, pos["x^5"]));
